css:
body{
    margin:0;
    padding:0;
    box-sizing:border-box;
}
#container{
    display:flex;
    justify-content:center;
}
.id-card{
    height:300px;
    border-radius:10px;
    width:500px;
    border:1px solid black;
    padding:10px;
}
#org-logo{
    width:70px;
    height:70px;
    object-fit:contain;
}
.parent{
    width:100%;
    height:58%;
    display:flex;
    justify-content:center;
    align-items:center;
    gap:5px;
}
#left-block{
    height:100%;
    width:35%;
    display:flex;
    justify-content:end;
    align-items:center;
}
#right-block{
    height:100%;
    width:64%;
    padding:5px;
    display:flex;
    flex-direction:column;
    justify-content:center;
    align-items:start;
    font-size:17px;
    line-height:17px;
    white-space: pre;
}
.user-photo{
    height:150px;
    width:120px;
    border-radius:10%;
    object-fit:cover;
    border:3px solid #C28413;
}
#sign-block{
    position:relative;
    display:flex;
    flex-direction:column;
    align-items:end;
    justify-content:end;
}
.signature{
    width:140px;
    height:40px;
    position:absolute;
    bottom:10px;
    right:0px;
    object-fit:contain;
}
