css:
body {
  font-family: Arial, sans-serif;
}
.id-card {
  width: 400px;
  height:220px;
  background-color: #fff;
  border-radius: 10px;
  box-shadow: 1px 4px 8px rgba(0, 0, 0, 0.1);
  padding: 20px;
  margin: 40px auto;
  position: relative; /* Ensure relative positioning for absolute positioning of logos */
}
.id-card img {
  width: 100%;
  border-radius: 50%;
  margin-bottom: 20px;
}
.id-card h2, .id-card p {
  margin: 0;
  text-align: center;
}
.img-parent{
    height:auto;
    width:100%;
    display:flex;
    align-items:center;
    justify-content:center;
}
.user-photo{

    max-width:50px!important;
    height:50px;

}
.id-card p {
  font-size: 14px;
  color: #666;
}
.id-card .logo {
  position: absolute;
  top: 10px;
  width: 50px;
  height: auto;
}
.id-card .logo.left {
  left: 10px;
}
.id-card .logo.right {
  right: 10px;
}
