css:
body {
  font-family: Arial, sans-serif;
}
.id-card {
  width: 400px;
  height:220px;
  background-color: #fff;
  border-radius: 10px;
  box-shadow: 1px 4px 8px rgba(0, 0, 0, 0.1);
  padding: 20px;
  margin: 40px auto;
  background: linear-gradient(135deg, #ffcccc, #ffffcc, #ccccff);
  position: relative; /* Ensure relative positioning for absolute positioning of logos */
}
.id-card img {
  width: 100%;
  background-attachment:contain;
  border-radius: 50%;
  margin-bottom: 10px;
}
.id-card h4{
  margin: 0px;
  text-align: center;
}
.img-parent{
    height:auto;
    width:100%;
    display:flex;
    align-items:center;
    justify-content:center;
}
.user-photo{

    max-width:50px!important;
    height:50px;

}
.id-card p {
  font-size: 12px;
  color: #746;
}
.id-card .logo {
  position: absolute;
  top: 10px;
  width: 75px;
  height: auto;
}
.id-card .logo.left {
  left: 10px;
  aspect-ratio: 1;
  object-fit: contain;
  mix-blend-mode: color-burn;
}
.id-card .logo.right {
  right: 10px;
}
.sign-image{
    height:40px!important;
    border-radius:0px!important;
    width:80%!important;
}
.details{
  display: flex;
  flex-direction: column;
  align-items: start;
  line-height:12px;
}
.signature{
  align-self:end;
  margin-top:-40px !important;
  line-height:8px;

}
